INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576147, 'Velpatasvir', 'Lansoprazole', 'Major', 'Coadministration of velpatasvir with proton pump inhibitors may reduce its gastrointestinal absorption.  Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317477, 'Concomitant use of sofosbuvir-velpatasvir with proton-pump inhibitors should generally be avoided.  If coadministration is required, sofosbuvir-velpatasvir should be administered with food and taken 4 hours before the proton-pump inhibitor at a maximum daily dose equivalent to omeprazole 20 mg.  Use with other proton-pump inhibitors has not been studied.', 'Absorption', 'Concomitant use of sofosbuvir-velpatasvir with proton-pump inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576148, 'Velpatasvir', 'Lemborexant', 'Moderate', 'Coadministration with lemborexant may decrease the plasma concentrations and pharmacologic effects of CYP450 2B6 substrates.  The proposed mechanism is lemborexant-mediated induction of the CYP450 2B6 isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if lemborexant is used concomitantly with drugs that are substrates of CYP450 2B6.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lemborexant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lemborexant is used concomitantly with drugs that are substrates of CYP450 2B6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576149, 'Velpatasvir', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or organic anion transporting polypeptide protein (OATP) 1B1 and 1B3.', 'DDInter', 1767317477, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy.  Moreover, clinicians should be aware that the magnitude of CYP450 3A- and OATP1B1/3-mediated drug interactions with coadministered drugs may be different when letermovir is used with cyclosporine.  The combined effect of the two drugs on CYP450 3A4 may be similar to that of a strong CYP450 3A4 inhibitor, hence clinicians should refer to the prescribing information for dosing recommendations of the CYP450 3A4 substrate with a strong CYP450 3A4 inhibitor.  Similarly, letermovir and cyclosporine may demonstrate some additive effects on OATP1B1 inhibition, although cyclosporine by itself is already a strong OATP1B1/3 inhibitor.', 'Metabolism', 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
