INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576160, 'Velpatasvir', 'Maraviroc', 'Moderate', 'Coadministration with inhibitors of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may increase the plasma concentrations of maraviroc, which is a substrate of both.', 'DDInter', 1767317477, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.  Dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576161, 'Velpatasvir', 'Metformin', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576162, 'Velpatasvir', 'Miglitol', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576163, 'Velpatasvir', 'Mitoxantrone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of mitoxantrone, which is a substrate of the efflux transporter also known as ABCB1 or MDR1.', 'DDInter', 1767317477, 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor.  Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576164, 'Velpatasvir', 'Morphine', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of morphine and its risk of adverse effects, including hypotension, respiratory and CNS depression, profound sedation, coma, and death.  The proposed mechanism may involve inhibition of the intestinal P-gp efflux transporter, resulting in enhanced oral bioavailability of morphine, a P-gp substrate.', 'DDInter', 1767317477, 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.  Close clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy, and the morphine dosage adjusted as necessary.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation that may be greater than otherwise expected.  In addition, patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them.', 'Absorption', 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576165, 'Velpatasvir', 'Naldemedine', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G.  Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.  Naldemedine is also a substrate of the P-gp efflux transporter.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).  Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576166, 'Velpatasvir', 'Naloxegol', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.', 'DDInter', 1767317477, 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.  When a single 25 mg dose of naloxegol was administered with a single 600 mg dose of quinidine, a potent P-gp inhibitor and weak CYP450 3A4 inhibitor, naloxegol peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 147% and 39%, respectively.  These changes are not considered clinically significant; therefore, no dosage adjustments are necessary.', 'Metabolism', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576167, 'Velpatasvir', 'Nateglinide', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576168, 'Velpatasvir', 'Nizatidine', 'Moderate', 'Coadministration of velpatasvir with H2-receptor antagonists may reduce its gastrointestinal absorption.  Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317477, 'H2-receptor antagonists may be administered simultaneously with or 12 hours apart from sofosbuvir-velpatasvir at a dose that does not exceed the equivalent of famotidine 40 mg twice daily.', 'Absorption', 'H2-receptor antagonists may be administered simultaneously with or 12 hours apart from sofosbuvir-velpatasvir at a dose that does not exceed the equivalent of famotidine 40 mg twice daily.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576169, 'Velpatasvir', 'Olaparib', 'Moderate', 'Coadministration with olaparib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 1A2 and/or 2B6.  The proposed mechanism, based on in vitro data, is increased clearance due to olaparib-mediated induction of CYP450 1A2 and/or 2B6.', 'DDInter', 1767317477, 'Caution is advised if olaparib is used concomitantly with drugs that are substrates of CYP450 1A2 and/or 2B6.  Pharmacologic response and serum levels should be monitored more closely whenever olaparib is added to or withdrawn from therapy with these drugs.  Dosage adjustments may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised if olaparib is used concomitantly with drugs that are substrates of CYP450 1A2 and/or 2B6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576170, 'Velpatasvir', 'Omeprazole', 'Major', 'Coadministration of velpatasvir with proton pump inhibitors may reduce its gastrointestinal absorption.  Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317477, 'Concomitant use of sofosbuvir-velpatasvir with proton-pump inhibitors should generally be avoided.  If coadministration is required, sofosbuvir-velpatasvir should be administered with food and taken 4 hours before the proton-pump inhibitor at a maximum daily dose equivalent to omeprazole 20 mg.  Use with other proton-pump inhibitors has not been studied.', 'Absorption', 'Concomitant use of sofosbuvir-velpatasvir with proton-pump inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
