INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576191, 'Velpatasvir', 'Simvastatin', 'Moderate', 'Coadministration with velpatasvir may increase the plasma concentrations of the HMG-CoA reductase inhibitors atorvastatin and simvastatin.  The proposed mechanism has not been delineated but may involve velpatasvir inhibition of the hepatic uptake transporter organic anion transporting polypeptide 1B (OATP1B), and the efflux transporters P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP), of which atorvastatin and simvastatin are substrates.', 'DDInter', 1767317477, 'The lowest dose of atorvastatin or simvastatin should be used when prescribed with sofosbuvir-velpatasvir, and the dosage titrated cautiously.  Pravastatin may be considered as a substitute.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by fever, malaise, and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Distribution', 'The lowest dose of atorvastatin or simvastatin should be used when prescribed with sofosbuvir-velpatasvir, and the dosage titrated cautiously.', NULL, NULL, 'Moderate', 'pharmacodynamic');
