INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576193, 'Velpatasvir', 'Sodium bicarbonate', 'Moderate', 'Concurrent administration of velpatasvir with drugs that elevate gastric pH such as antacids may reduce its gastrointestinal absorption.  Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH.  Drugs that increase gastric pH are expected to decrease the plasma concentration of velpatasvir.', 'DDInter', 1767317477, 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of sofosbuvir-velpatasvir.  The same precaution may be applicable to other drugs with antacid effects.', 'Absorption', 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of sofosbuvir-velpatasvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
