INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576195, 'Velpatasvir', 'Somapacitan', 'Moderate', 'Coadministration with human growth hormone may decrease the plasma concentrations of drugs that are metabolized by CYP450 3A4.  Data from an interaction study performed in growth hormone deficient adults suggest that growth hormone (somatropin) administration can increase the clearance of compounds known to be metabolized by cytochrome P450 isoenzymes, especially CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever human growth hormone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
