INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576203, 'Velpatasvir', 'Topotecan', 'Moderate', 'Coadministration with velpatasvir may increase the plasma concentrations of topotecan.  Although not studied, a clinically significant pharmacokinetic interaction may be anticipated because topotecan is a substrate of P-glycoprotein (P-gp) and breast cancer resistance protein (BCRP), and velpatasvir inhibits both of these drug transporters acc', 'DDInter', 1767317477, 'Concomitant use of topotecan with sofosbuvir-velpatasvir is not recommended.', 'Others', 'Concomitant use of topotecan with sofosbuvir-velpatasvir is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
