INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576271, 'Glecaprevir', 'Binimetinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib.  The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', 'DDInter', 1767317477, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.  Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
