INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576273, 'Glecaprevir', 'Bosutinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of bosutinib, which is a substrate of the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when bosutinib is used with CYP450 3A4 or P-gp inhibitors.  Patients should be monitored more closely for development of adverse effects such as diarrhea, nausea, vomiting, abdominal pain, myelosuppression, hepatotoxicity, and fluid retention (e.g., pericardial effusion, pleural effusion, pulmonary edema, peripheral edema).', 'Metabolism', 'Caution is advised when bosutinib is used with CYP450 3A4 or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576274, 'Glecaprevir', 'Brentuximab vedotin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.  In vitro, MMAE was found to be a substrate of the P-gp efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when brentuximab is used with P-gp inhibitors.  Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended.', 'Excretion', 'Caution is advised when brentuximab is used with P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
