INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576280, 'Glecaprevir', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of P-glycoprotein (P-gp) or CYP450 3A4 may significantly decrease the plasma concentrations of glecaprevir and pibrentasvir.  Both antiviral agents are substrates of the P-gp efflux transporter, and glecaprevir is additionally a substrate of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of glecaprevir and pibrentasvir with potent P-gp or CYP450 3A4 inducers is not recommended.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of glecaprevir and pibrentasvir with potent P-gp or CYP450 3A4 inducers is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
