INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576288, 'Glecaprevir', 'Darolutamide', 'Moderate', 'Coadministration with darolutamide may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3.  The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by darolutamide.', 'DDInter', 1767317477, 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darolutamide is added to or withdrawn from therapy.', 'Metabolism, Distribution', 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576289, 'Glecaprevir', 'Darunavir', 'Major', 'Coadministration with boosted darunavir or lopinavir/ritonavir may significantly increase the plasma concentrations of glecaprevir.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir by darunavir, lopinavir, and ritonavir.  Inhibition of intestinal and/or biliary efflux of glecaprevir via P-glycoprotein (P-gp) and breast cancer resistance protein (BCRP) may also contribute.', 'DDInter', 1767317477, 'Concomitant use of glecaprevir with lopinavir-ritonavir or darunavir boosted with either ritonavir or cobicistat is not recommended.', 'Metabolism, Absorption, Distribution', 'Concomitant use of glecaprevir with lopinavir-ritonavir or darunavir boosted with either ritonavir or cobicistat is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576290, 'Glecaprevir', 'Daunorubicin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1.', 'DDInter', 1767317477, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.  Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
