INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576305, 'Glecaprevir', 'Esomeprazole', 'Moderate', 'Coadministration with omeprazole may decrease the plasma concentrations of glecaprevir.  The mechanism of interaction has not been established.', 'DDInter', 1767317477, 'According to the manufacturer, no dosage adjustment is necessary for glecaprevir-pibrentasvir during coadministration with omeprazole.  However, the potential for diminished pharmacologic effects of glecaprevir-pibrentasvir should be considered during coadministration with higher dosages of omeprazole.  Alternative treatments may be required if an interaction is suspected.', 'Others', 'According to the manufacturer, no dosage adjustment is necessary for glecaprevir-pibrentasvir during coadministration with omeprazole.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576306, 'Glecaprevir', 'Ethinylestradiol', 'Major', 'Coadministration of ethinyl estradiol with glecaprevir-pibrentasvir may increase the risk of alanine aminotransferase (ALT) elevations.  In clinical trials, women using ethinyl estradiol-containing medications such as combined oral contraceptives were significantly more likely to have ALT elevations, in some cases greater than 20 times the upper limit of normal.  ALT elevations are also associated with glecaprevir, particularly at high exposures, thus an additive risk may occur.  Pharmacokinetically, glecaprevir-pibrentasvir may increase the plasma concentrations of ethinyl estradiol, presumably due to mild inhibition of CYP450 3A4-mediated metabolism.', 'DDInter', 1767317477, 'Concomitant use of ethinyl estradiol-containing medications with glecaprevir-pibrentasvir is not recommended and is considered contraindicated by some authorities.', 'Metabolism, Synergism', 'Concomitant use of ethinyl estradiol-containing medications with glecaprevir-pibrentasvir is not recommended and is considered contraindicated by some authorities.', NULL, NULL, 'Major', 'pharmacodynamic');
