INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576311, 'Glecaprevir', 'Fidaxomicin', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', 'DDInter', 1767317477, 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.  When fidaxomicin 200 mg was administered in combination with cyclosporine 200 mg in 14 study subjects, fidaxomicin peak plasma concentration (Cmax) and systemic exposure (AUC) increased by an average of 4.2- and 1.9-fold, respectively, compared to when administered alone.  The Cmax and AUC of OP-1118 was increased by 9.5- and 4.1-fold, respectively, with cyclosporine.  Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.  However, concomitant use of a P-gp inhibitor had no attributable effect on safety or efficacy of fidaxomicin in controlled clinical trials.  No dosage adjustment is recommended when fidaxomicin is coadministered with P-gp inhibitors.', 'Absorption, Excretion', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576312, 'Glecaprevir', 'Fluvastatin', 'Moderate', 'Coadministration with glecaprevir-pibrentasvir may increase the plasma concentrations of all statins including fluvastatin and pitavastatin.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of statins by glecaprevir and pibrentasvir.  Although the interaction has not been studied specifically with fluvastatin or pitavastatin, it has been reported with other statins that are also substrates of OATP1B1.  High levels of HMG-CoA reductase inhibitory activity in plasma may be associated with an increased risk of musculoskeletal toxicity.', 'DDInter', 1767317477, 'Caution is advised during concomitant administration of fluvastatin or pitavastatin with glecaprevir-pibrentasvir.  The lowest approved dosage of the statin should be used.  If higher dosages are required, the lowest effective statin dosage should be prescribed based on a risk/benefit assessment.', 'Metabolism, Distribution', 'Caution is advised during concomitant administration of fluvastatin or pitavastatin with glecaprevir-pibrentasvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
