INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576314, 'Glecaprevir', 'Fostemsavir', 'Moderate', 'Coadministration with fostemsavir may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP).  Temsavir, the active moiety of fostemsavir, is an inhibitor of OATP 1B1 and 1B3.  Additionally, temsavir and two metabolites are inhibitors of BCRP.', 'DDInter', 1767317477, 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostemsavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576315, 'Glecaprevir', 'Gilteritinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of gilteritinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.  Patients should be monitored for adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections and stomatitis, and the gilteritinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
