INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576344, 'Glecaprevir', 'Lovastatin', 'Major', 'Coadministration with glecaprevir-pibrentasvir may significantly increase the plasma concentrations of lovastatin and its active metabolites.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of the statins and their metabolites by glecaprevir and pibrentasvir.  Inhibition of CYP450 3A4-mediated metabolism may also contribute, as glecaprevir and pibrentasvir are weak inhibitors of the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of lovastatin or red yeast rice (which contains lovastatin) with glecaprevir-pibrentasvir is not recommended.  Other statins may be used with glecaprevir-pibrentasvir, but a dosage reduction may be required.', 'Metabolism', 'Concomitant use of lovastatin or red yeast rice (which contains lovastatin) with glecaprevir-pibrentasvir is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576345, 'Glecaprevir', 'Lusutrombopag', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of lusutrombopag, which is a substrate of both efflux transporter proteins.', 'DDInter', 1767317477, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.  Pharmacologic response to lusutrombopag should be monitored more closely following initiation, discontinuation, or change of dosage of the concomitant P-gp or BCRP inhibitor.', 'Metabolism', 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
