INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576357, 'Glecaprevir', 'Naloxegol', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.', 'DDInter', 1767317477, 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.  When a single 25 mg dose of naloxegol was administered with a single 600 mg dose of quinidine, a potent P-gp inhibitor and weak CYP450 3A4 inhibitor, naloxegol peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 147% and 39%, respectively.  These changes are not considered clinically significant; therefore, no dosage adjustments are necessary.', 'Metabolism', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.', NULL, NULL, 'Minor', 'pharmacodynamic');
