INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576391, 'Glecaprevir', 'Simvastatin', 'Major', 'Coadministration with glecaprevir-pibrentasvir may significantly increase the plasma concentrations of atorvastatin, simvastatin, and their active acid metabolites.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of the statins and their metabolites by glecaprevir and pibrentasvir.  Inhibition of CYP450 3A4-mediated metabolism may also contribute, as glecaprevir and pibrentasvir are weak inhibitors of the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of atorvastatin or simvastatin with glecaprevir-pibrentasvir is not recommended.  Other statins may be used with glecaprevir-pibrentasvir, but a dosage reduction may be required.', 'Metabolism, Distribution', 'Concomitant use of atorvastatin or simvastatin with glecaprevir-pibrentasvir is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576392, 'Glecaprevir', 'Sirolimus', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the blood concentrations of the macrolide immunosuppressants sirolimus and tacrolimus, both of which are metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576393, 'Glecaprevir', 'Sitagliptin', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576394, 'Glecaprevir', 'Somapacitan', 'Moderate', 'Coadministration with inducers of P-glycoprotein (P-gp) or CYP450 3A4 may decrease the plasma concentrations of glecaprevir and pibrentasvir.  Both antiviral agents are substrates of the P-gp efflux transporter, and glecaprevir is additionally a substrate of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
