INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576429, 'Atorvastatin', 'Ledipasvir', 'Moderate', 'Coadministration of HMG-CoA reductase inhibitors (statins) with HCV NS5A/NS5B inhibitors, such as ledipasvir, may significantly increase the plasma concentrations of the statin and the risk of adverse effects, such as myopathy and rhabdomyolysis.  The proposed mechanism of this interaction has not been clearly delineated but may involve ledipasvir-mediated inhibition of the metabolism of statins via hepatic transport enzymes and/or CYP450 isoenzymes.', 'DDInter', 1767317477, 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered.  Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, fever, malaise, and/or dark colored urine, particularly during the initial months of therapy and during any periods of upward dosage titration of either drug.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576430, 'Berotralstat', 'Ledipasvir', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat.  Berotralstat is a substrate of both P-gp and BCRP.', 'DDInter', 1767317477, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.  Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea.  Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576431, 'Betrixaban', 'Ledipasvir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when betrixaban is used with P-gp inhibitors.  Closer monitoring of the pharmacologic effects of betrixaban may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy.  Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).  Concomitant use of betrixaban with P-gp inhibitors should be avoided in patients with severe renal impairment (CrCl<30 mL/min).', 'Excretion', 'Caution is advised when betrixaban is used with P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
