INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576445, 'Dexlansoprazole', 'Ledipasvir', 'Moderate', 'Coadministration of ledipasvir with proton pump inhibitors may reduce its gastrointestinal absorption.  Ledipasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317477, 'Proton-pump inhibitors at doses comparable to omeprazole 20 mg or lower should only be administered either simultaneously with or up to 2 hours after ledipasvir-sofosbuvir under fasted conditions.  Some authorities advise against administering proton pump inhibitors before ledipasvir-containing products; however, a timeframe of dosage separation has not been specified.', 'Absorption', 'Proton-pump inhibitors at doses comparable to omeprazole 20 mg or lower should only be administered either simultaneously with or up to 2 hours after ledipasvir-sofosbuvir under fasted conditions.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576446, 'Dicoumarol', 'Ledipasvir', 'Moderate', 'Liver function changes due to treatment with direct-acting HCV antiviral agents may affect the therapeutic efficacy of vitamin K antagonists.  The mechanism may involve potential pharmacokinetic changes for some vitamin K antagonists due to altered metabolism, or patients may exhibit alterations in hepatic synthesis of vitamin K-dependent coagulation factors and clearance of activated factors that could impact response to vitamin K antagonists.', 'DDInter', 1767317477, 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', 'Others', 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576447, 'Digoxin', 'Ledipasvir', 'Moderate', 'Coadministration with ledipasvir may increase the oral bioavailability of digoxin.  The proposed mechanism is ledipasvir inhibition of the P-glycoprotein-mediated intestinal efflux of digoxin.', 'DDInter', 1767317477, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of ledipasvir.  Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of ledipasvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
