INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576470, 'Indacaterol', 'Ledipasvir', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the systemic exposure to indacaterol following oral inhalation, as it is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'Indacaterol oral inhalation powder has been evaluated in clinical trials for up to one year at doses up to 600 mcg.  No dosage adjustment is necessary at the 75 mcg dose when used with CYP450 3A4 and P-glycoprotein inhibitors.', 'Metabolism', 'Indacaterol oral inhalation powder has been evaluated in clinical trials for up to one year at doses up to 600 mcg.', NULL, NULL, 'Minor', 'pharmacodynamic');
