INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576483, 'Ledipasvir', 'Lansoprazole', 'Moderate', 'Coadministration of ledipasvir with proton pump inhibitors may reduce its gastrointestinal absorption.  Ledipasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317477, 'Proton-pump inhibitors at doses comparable to omeprazole 20 mg or lower should only be administered either simultaneously with or up to 2 hours after ledipasvir-sofosbuvir under fasted conditions.  Some authorities advise against administering proton pump inhibitors before ledipasvir-containing products; however, a timeframe of dosage separation has not been specified.', 'Absorption', 'Proton-pump inhibitors at doses comparable to omeprazole 20 mg or lower should only be administered either simultaneously with or up to 2 hours after ledipasvir-sofosbuvir under fasted conditions.', NULL, NULL, 'Moderate', 'pharmacodynamic');
