INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576485, 'Ledipasvir', 'Lasmiditan', 'Moderate', 'Coadministration with lasmiditan may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters.  The proposed mechanism, based on in vitro data, involves decreased clearance due to inhibition of P-gp and BCRP by lasmiditan.', 'DDInter', 1767317477, 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible.  If coadministration is required, patients should be carefully monitored for increased adverse reactions of these drugs and dosing adjusted as necessary.', 'Metabolism', 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576486, 'Ledipasvir', 'Lefamulin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-gp may increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter.  When oral lefamulin was administered with oral ketoconazole, a potent CYP450 3A4/P-gp inhibitor, mean lefamulin peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 58% and 165%, respectively.', 'DDInter', 1767317477, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors.  Patients should be monitored for increased adverse effects and seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
