INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576492, 'Lovastatin', 'Ledipasvir', 'Moderate', 'Coadministration of HMG-CoA reductase inhibitors (statins) with HCV NS5A/NS5B inhibitors, such as ledipasvir, may significantly increase the plasma concentrations of the statin and the risk of adverse effects, such as myopathy and rhabdomyolysis.  The proposed mechanism of this interaction has not been clearly delineated but may involve ledipasvir-mediated inhibition of the metabolism of statins via hepatic transport enzymes and/or CYP450 isoenzymes.', 'DDInter', 1767317477, 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered.  Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, fever, malaise, and/or dark colored urine, particularly during the initial months of therapy and during any periods of upward dosage titration of either drug.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
