INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576501, 'Morphine', 'Ledipasvir', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of morphine and its risk of adverse effects, including hypotension, respiratory and CNS depression, profound sedation, coma, and death.  The proposed mechanism may involve inhibition of the intestinal P-gp efflux transporter, resulting in enhanced oral bioavailability of morphine, a P-gp substrate.', 'DDInter', 1767317477, 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.  Close clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy, and the morphine dosage adjusted as necessary.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation that may be greater than otherwise expected.  In addition, patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them.', 'Absorption', 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576502, 'Naldemedine', 'Ledipasvir', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G.  Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.  Naldemedine is also a substrate of the P-gp efflux transporter.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).  Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576503, 'Naloxegol', 'Ledipasvir', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.', 'DDInter', 1767317477, 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.  When a single 25 mg dose of naloxegol was administered with a single 600 mg dose of quinidine, a potent P-gp inhibitor and weak CYP450 3A4 inhibitor, naloxegol peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 147% and 39%, respectively.  These changes are not considered clinically significant; therefore, no dosage adjustments are necessary.', 'Metabolism', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of naloxegol, which is a substrate of both the efflux transporter and the isoenzyme.', NULL, NULL, 'Minor', 'pharmacodynamic');
