INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576539, 'Sodium bicarbonate', 'Ledipasvir', 'Moderate', 'Concurrent administration of ledipasvir with drugs that elevate gastric pH such as antacids may reduce its gastrointestinal absorption.  Ledipasvir exhibits pH-dependent solubility, with increased solubility at lower pH.  Drugs that increase gastric pH are expected to decrease the plasma concentration of ledipasvir.', 'DDInter', 1767317477, 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of ledipasvir-sofosbuvir.  The same precaution may be applicable to other drugs with antacid effects.', 'Absorption', 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of ledipasvir-sofosbuvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
