INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576541, 'Sodium zirconium cyclosilicate', 'Ledipasvir', 'Moderate', 'Because sodium zirconium cyclosilicate can transiently increase gastric pH, it may affect the absorption of coadministered medications that exhibit pH-dependent solubility.', 'DDInter', 1767317477, 'ADJUST DOSING INTERVAL:  Because sodium zirconium cyclosilicate can transiently increase gastric pH, it may affect the absorption of coadministered medications that exhibit pH-dependent solubility.  Altered efficacy or safety of these medications may occur when they are administered too close to the dosing of sodium zirconium cyclosilicate.  According to the product labeling, 39 drugs were tested to determine potential interactions with sodium zirconium cyclosilicate.  Drugs that did not show an in vitro interaction with sodium zirconium cyclosilicate were allopurinol, apixaban, aspirin, captopril, cyclosporine, digoxin, ethyl estradiol, lisinopril, magnesium, metformin, phenytoin, prednisone, propranolol, quinapril, spironolactone, and ticagrelor.  Of the 23 drugs that showed an in vitro interaction, nine were subsequently tested in healthy volunteers.  Losartan, glipizide, and levothyroxine did not demonstrate an in vivo interaction with sodium zirconium cyclosilicate.  However, an increase in systemic exposure was observed for weak acids such as furosemide and atorvastatin when coadministered with sodium zirconium cyclosilicate, while a decrease in systemic exposure was observed for weak bases such as dabigatran.', 'Absorption', 'ADJUST DOSING INTERVAL:  Because sodium zirconium cyclosilicate can transiently increase gastric pH, it may affect the absorption of coadministered medications that exhibit pH-dependent solubility.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576542, 'Talazoparib', 'Ledipasvir', 'Moderate', 'Coadministration with sparsentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2B6, 2C9, and 2C19 isoenzymes.  The proposed mechanism is increased clearance due to sparsentan-mediated induction of CYP450 2B6, 2C9, and 2C19.', 'DDInter', 1767317477, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.  However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Metabolism', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
