    ) VALUES (1576625, 'Homatropine', 'Eluxadoline', 'Moderate', 'The risk of constipation and serious constipation-related adverse reactions may be increased when eluxadoline is used with other drugs that are also associated with this adverse effect.', NULL, 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided.', NULL, 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided.  Loperamide may be used occasionally for acute management of severe diarrhea, but chronic use is not recommended, and it should be discontinued immediately if constipation occurs.  Eluxadoline should also be discontinued if constipation occurs for more than 4 days.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
