INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577173, 'Brentuximab vedotin', 'Rifampicin', 'Moderate', 'Coadministration with rifampin may decrease the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.  MMAE is primarily metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'The potential for diminished efficacy of brentuximab vedotin and increased risk of hepatic injury should be considered when brentuximab vedotin is used with rifampin.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Metabolism', 'The potential for diminished efficacy of brentuximab vedotin and increased risk of hepatic injury should be considered when brentuximab vedotin is used with rifampin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577174, 'Isoniazid', 'Brentuximab vedotin', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317477, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.  Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent neuropathy or hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577175, 'Dimethyl fumarate', 'Rifampicin', 'Moderate', 'The use of fumaric acid esters in combination with medications that can cause nephrotoxicity (e.g., aminoglycosides, diuretics, nonsteroidal anti-inflammatory drugs, cyclosporine, lithium, methotrexate) may increase the potential for adverse renal reactions.', 'DDInter', 1767317477, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.  Assessment of renal function (e.g., serum creatinine, blood urea nitrogen, urinalysis) is recommended prior to initiating treatment with fumaric acid esters and as clinically indicated during treatment.', 'Synergism', 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577176, 'Diroximel fumarate', 'Rifampicin', 'Moderate', 'The use of fumaric acid esters in combination with medications that can cause nephrotoxicity (e.g., aminoglycosides, diuretics, nonsteroidal anti-inflammatory drugs, cyclosporine, lithium, methotrexate) may increase the potential for adverse renal reactions.', 'DDInter', 1767317477, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.  Assessment of renal function (e.g., serum creatinine, blood urea nitrogen, urinalysis) is recommended prior to initiating treatment with fumaric acid esters and as clinically indicated during treatment.', 'Synergism', 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577177, 'Gadoxetic acid', 'Rifampicin', 'Moderate', 'Anionic drugs such as rifamycins may interfere with the hepatic contrast enhancement of gadoxetate by competing for biliary excretion.  In animal studies, rifampin blocked the hepatic uptake and decreased the hepatic contrast effect of gadoxetate.', 'DDInter', 1767317477, 'Clinicians should be aware of possible interference with gadoxetate imaging in patients receiving rifamycin therapy.', 'Distribution', 'Clinicians should be aware of possible interference with gadoxetate imaging in patients receiving rifamycin therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577178, 'Rifampicin', 'Monomethyl fumarate', 'Moderate', 'The use of fumaric acid esters in combination with medications that can cause nephrotoxicity (e.g., aminoglycosides, diuretics, nonsteroidal anti-inflammatory drugs, cyclosporine, lithium, methotrexate) may increase the potential for adverse renal reactions.', 'DDInter', 1767317477, 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.  Assessment of renal function (e.g., serum creatinine, blood urea nitrogen, urinalysis) is recommended prior to initiating treatment with fumaric acid esters and as clinically indicated during treatment.', 'Synergism', 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577179, 'Abemaciclib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of abemaciclib and its pharmacologically active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of abemaciclib with potent CYP450 3A4 inducers should generally be avoided.  Alternative agents without CYP450 3A4 induction potential should be considered.', 'Metabolism', 'Concomitant use of abemaciclib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577180, 'Abiraterone', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of abiraterone, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided.  Otherwise, an increase in dosing frequency for abiraterone acetate to twice daily during the coadministration period (e.g., from 1000 mg once daily to 1000 mg twice daily) may be considered, although this dosage adjustment has not been studied clinically.  The dosage should be adjusted back to the previous level when the CYP450 3A4 inducer is discontinued.', 'Metabolism', 'Concomitant use of abiraterone acetate with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577181, 'Acalabrutinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of acalabrutinib with potent CYP450 3A4 inducers should generally be avoided.  If coadministration cannot be avoided, the manufacturer recommends increasing the dosage of acalabrutinib to 200 mg twice daily.', 'Metabolism', 'Concomitant use of acalabrutinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577182, 'Acetohexamide', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577183, 'Alfentanil', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of alfentanil, which appears to be extensively metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Patients who are currently receiving or have recently received drugs that induce CYP450 3A4 should be monitored for potentially decreased response to alfentanil, and the dosage adjusted as necessary.', 'Metabolism', 'Patients who are currently receiving or have recently received drugs that induce CYP450 3A4 should be monitored for potentially decreased response to alfentanil, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577184, 'Alpelisib', 'Lumacaftor', 'Major', 'Coadministration of alpelisib with potent inducers of CYP450 3A4 may significantly reduce the plasma concentrations of alpelisib.  The proposed mechanism, based on in vitro data, is increased clearance due to induction of CYP450 3A4, which is one of the routes of metabolism of alpelisib.', 'DDInter', 1767317477, 'According to the manufacturer, concomitant use of alpelisib with potent CYP450 3A4 inducers should generally be avoided.  Alternative therapeutic agents with less enzyme induction potential should be considered.', 'Metabolism', 'According to the manufacturer, concomitant use of alpelisib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577185, 'Amiodarone', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577186, 'Amlodipine', 'Lumacaftor', 'Major', 'Potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of calcium channel blockers, the majority of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation or discontinuation of the CYP450 3A4 inducer, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577187, 'Amprenavir', 'Lumacaftor', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.  Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended.  Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577188, 'Lumacaftor', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations of drugs that are substrates of the metabolic enzymes CYP450 3A4, CYP450 2C19, CYP450 2C9, and uridine diphosphate glucuronosyltransferase (UGT), as well as substrates of the membrane transporters organic anion transporting polypeptide (OATP) 1B1, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', 'DDInter', 1767317477, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.  Substitution for these medications is recommended when possible, or monitor for potential loss of therapeutic efficacy if coadministration is required.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a CYP450 inducer like apalutamide and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577189, 'Aripiprazole', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of aripiprazole and its active metabolite, dehydro-aripiprazole.', 'DDInter', 1767317477, 'When aripiprazole is administered orally, the prescribing information recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.  Additional dosage adjustments should be based on clinical evaluation.  Upon discontinuation of the inducer, aripiprazole dosage should be reduced to the original level over 1 to 2 weeks.  When given as an immediate-acting intramuscular injection, the aripiprazole dosage should also be doubled in patients receiving potent CYP450 3A4 inducers.  For patients receiving extended-release intramuscular formulations of aripiprazole, concomitant use of potent CYP450 3A4 inducers should generally be avoided for greater than 14 days, although some formulations may be administered, with or without a dosage adjustment depending on the dose.  The prescribing information for individual aripiprazole products should be consulted for specific recommendations regarding concomitant use with potent CYP450 3A4 inducers.', 'Metabolism', 'When aripiprazole is administered orally, the prescribing information recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577190, 'Armodafinil', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.  Dose adjustments may be required if an interaction is suspected.', 'Metabolism', 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577191, 'Atazanavir', 'Lumacaftor', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.  Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended.  Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577192, 'Atorvastatin', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of atorvastatin and its active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.  Otherwise, pharmacologic response to atorvastatin should be closely monitored, and the dosage adjusted as necessary.  A statin that is not metabolized by CYP450 3A4 such as fluvastatin, pitavastatin, pravastatin, or rosuvastatin may also be substituted for atorvastatin when used with certain enzyme inducers.', 'Metabolism', 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577193, 'Avapritinib', 'Lumacaftor', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', 'DDInter', 1767317477, 'Concomitant use of avapritinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of avapritinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
