INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577194, 'Avatrombopag', 'Lumacaftor', 'Moderate', 'Concomitant use of avatrombopag with strong CYP450 3A4/5 or CYP450 2C9 inducers may decrease the systemic exposure (AUC) of avatrombopag which may lead to reduced efficacy.', 'DDInter', 1767317477, 'No dose adjustment is recommended; however, it may be advisable to monitor platelet count for signs of decreased efficacy of avatrombopag.', 'Metabolism', 'No dose adjustment is recommended; however, it may be advisable to monitor platelet count for signs of decreased efficacy of avatrombopag.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577195, 'Axitinib', 'Lumacaftor', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of axitinib with potent CYP450 3A4 inducers such as apalutamide, carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided.  Although pharmacokinetic data are lacking, the manufacturer recommends that moderate CYP450 3A4 inducers should also be avoided if possible, including but not limited to bosentan, cenobamate, dabrafenib, dexamethasone, efavirenz, etravirine, lorlatinib, modafinil, nafcillin, pexidartinib, and sotorasib.', 'Metabolism', 'Concomitant use of axitinib with potent CYP450 3A4 inducers such as apalutamide, carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577196, 'Bedaquiline', 'Lumacaftor', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to the major N-monodesmethyl metabolite, M2.', 'DDInter', 1767317477, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577197, 'Betamethasone', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577198, 'Betrixaban', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of P-glycoprotein (P-gp) may reduce the systemic exposure and pharmacodynamic effects of betrixaban, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'The use of betrixaban with P-gp inducers should generally be avoided.  If use is unavoidable, pharmacologic response to betrixaban should be monitored closely.', 'Absorption', 'The use of betrixaban with P-gp inducers should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577199, 'Bexarotene', 'Lumacaftor', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bexarotene.  In vitro studies suggest that bexarotene is metabolized by CYP450 3A4.  However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo.', 'DDInter', 1767317477, 'Although studies may now indicate that CYP450 3A4 inducers or inhibitors do not have a significant effect on bexarotene metabolism, we still conservatively recommend avoiding the concomitant use of strong CYP450 3A4 inducers (e.g., carbamazepine, phenytoin, phenobarbital, rifampin) when taking bexarotene.', 'Metabolism', 'Although studies may now indicate that CYP450 3A4 inducers or inhibitors do not have a significant effect on bexarotene metabolism, we still conservatively recommend avoiding the concomitant use of strong CYP450 3A4 inducers (e.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577200, 'Binimetinib', 'Lumacaftor', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib.  The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', 'DDInter', 1767317477, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.  Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577201, 'Boceprevir', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of boceprevir, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577202, 'Bosentan', 'Lumacaftor', 'Moderate', 'Coadministration with potent CYP450 3A4 and/or 2C9 inducers may decrease the plasma concentrations and therapeutic effects of bosentan.  The proposed mechanism is increased clearance of bosentan by induction of CYP450 3A4 and/or 2C9, the isoenzymes responsible for the metabolic clearance of bosentan.', 'DDInter', 1767317477, 'Until more information is available, the possibility of a diminished therapeutic response to bosentan should be considered when it is coadministered with a potent CYP450 3A4 and/or 2C9 inducer.  Clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 and/or 2C9 inducer is added to or withdrawn from therapy, and the bosentan dosage adjusted as necessary.', 'Metabolism', 'Until more information is available, the possibility of a diminished therapeutic response to bosentan should be considered when it is coadministered with a potent CYP450 3A4 and/or 2C9 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577203, 'Brexpiprazole', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of brexpiprazole.  In vitro and in vivo data indicate that brexpiprazole is metabolized primarily by CYP450 3A4 and 2D6.', 'DDInter', 1767317477, 'The prescribing information for brexpiprazole recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.  Additional dosage adjustments should be based on clinical evaluation.  Upon discontinuation of the inducer, brexpiprazole dosage should be reduced to the original level over 1 to 2 weeks.', 'Metabolism', 'The prescribing information for brexpiprazole recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577204, 'Brigatinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of brigatinib with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of brigatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577205, 'Bromocriptine', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bromocriptine.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of bromocriptine should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of bromocriptine should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577206, 'Budesonide', 'Lumacaftor', 'Moderate', 'Concomitant use of orally and rectally administered formulations of budesonide with inducers of CYP450 3A4 may decrease budesonide systemic as well as local exposure at the gut mucosa.  Budesonide undergoes extensive first-pass and systemic metabolism via intestinal and hepatic CYP450 3A4', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of orally and rectally administered budesonide should be considered during concomitant treatment with drugs that induce CYP450 3A4.  Alternative treatments or budesonide dose adjustments may be required if an interaction is suspected.', 'Metabolism, Absorption', 'The potential for diminished pharmacologic effects of orally and rectally administered budesonide should be considered during concomitant treatment with drugs that induce CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577207, 'Buprenorphine', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of buprenorphine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to buprenorphine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to buprenorphine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577208, 'Bupropion', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 2B6 may decrease the plasma concentrations of bupropion.  In vitro findings suggest that CYP450 2B6 is the principal isoenzyme involved in the formation of hydroxybupropion, one of three active metabolites of bupropion.', 'DDInter', 1767317477, 'Pharmacologic response to bupropion should be monitored more closely whenever a CYP450 2B6 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.  Concomitant use of potent and moderate CYP450 2B6 inducers such as carbamazepine, efavirenz, rifampin, ritonavir, and lopinavir-ritonavir should be avoided with fixed-dose combination products such as bupropion-naltrexone or bupropion-dextromethorphan.  For other bupropion products, increases in bupropion dosage should be guided by clinical response; however, the maximum recommended dosage should not be exceeded.', 'Metabolism', 'Pharmacologic response to bupropion should be monitored more closely whenever a CYP450 2B6 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577209, 'Buspirone', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations and pharmacologic effects of buspirone, which is primarily metabolized by the isoenzyme.  Conversely, discontinuation of an inducer may increase buspirone plasma concentrations and increase the risk of central nervous system depression and other adverse effects.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with potent CYP450 3A4 inducers.  Pharmacologic response to buspirone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buspirone dosage adjusted as necessary.', 'Metabolism', 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with potent CYP450 3A4 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577210, 'Butorphanol', 'Lumacaftor', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone.  Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer.  Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317477, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577211, 'Cabazitaxel', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as apalutamide, carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided.  The extent to which other, less potent CYP450 3A4 inducers may interact with cabazitaxel is unknown.  Caution is advised if they are used with cabazitaxel.', 'Metabolism', 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as apalutamide, carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577212, 'Cabozantinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317477, 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible.  If chronic coadministration is required, the daily dosage of cabozantinib should be increased in accordance with the prescribing information for the specific product.  For the treatment of progressive, metastatic medullary thyroid cancer (capsule formulation), the recommendation is to increase cabozantinib dosage by 40 mg (e.g., from 140 mg to 180 mg daily or from 100 mg to 140 mg daily) as tolerated, not to exceed 180 mg/day.  For the treatment of advanced renal cell carcinoma (RCC) in patients who have received prior anti-angiogenic therapy (tablet formulation), the recommendation is to increase cabozantinib dosage by 20 mg (e.g., from 60 mg to 80 mg daily or from 40 mg to 60 mg daily) as tolerated, not to exceed 80 mg/day.  The original dosage of cabozantinib may be resumed 2 to 3 days after discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577213, 'Cannabidiol', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 and/or CYP450 2C19 may decrease the plasma concentrations of cannabidiol.  The proposed mechanism of action is induction of CYP450 3A4 and/or 2C19-mediated metabolism of cannabidiol.', 'DDInter', 1767317477, 'A dosage increase of cannabidiol up to 2-fold may be required when coadministered with potent inducers of CYP450 3A4 and/or CYP450 2C19 based on clinical response and tolerability.', 'Metabolism', 'A dosage increase of cannabidiol up to 2-fold may be required when coadministered with potent inducers of CYP450 3A4 and/or CYP450 2C19 based on clinical response and tolerability.', NULL, NULL, 'Moderate', 'pharmacodynamic');
