INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577223, 'Chloroquine', 'Lumacaftor', 'Moderate', 'Coadministration with strong inducers of CYP450 2C8 or CYP450 3A4 may decrease the plasma concentrations and therapeutic effects of hydroxychloroquine.  When rifampin, a moderate CYP450 2C8 and potent CYP450 3A4 inducer, was administered concomitantly with hydroxychloroquine, a lack of efficacy of hydroxychloroquine was reported.  Because hydroxychloroquine and chloroquine and have similar structures and metabolic elimination pathways, a similar interaction may be observed with chloroquine.', 'DDInter', 1767317477, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a potent CYP450 3A4 or 2C8 inducer.  Clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 or 2C8 inducer is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary.', 'Metabolism', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a potent CYP450 3A4 or 2C8 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
