INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577227, 'Clarithromycin', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  Lumacaftor is a potent CYP450 3A4 inducer in vivo.', 'DDInter', 1767317477, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.g., oral midazolam, triazolam, lovastatin, simvastatin) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus).  Lumacaftor should also be avoided in patients receiving certain antibiotics (e.g., clarithromycin, erythromycin, telithromycin) and azole antifungal agents (e.g., itraconazole, ketoconazole, posaconazole, voriconazole).  Alternatives to these agents should be considered.  Some authorities recommend avoiding coadministration of lumacaftor-ivacaftor and itraconazole from 2 weeks before, during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577228, 'Clindamycin', 'Lumacaftor', 'Moderate', 'Coadministration with strong CYP450 3A4 inducers may decrease plasma concentrations and antimicrobial effects of clindamycin, which is metabolized predominantly by the isoenzyme.', 'DDInter', 1767317477, 'Monitor closely for diminished therapeutic response to clindamycin during coadministration with strong CYP450 3A4 inducers.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy with clindamycin.  An alternative agent with no or minimal CYP450 3A4-inducing activity may also be considered.', 'Metabolism', 'Monitor closely for diminished therapeutic response to clindamycin during coadministration with strong CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
