INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577238, 'Dabrafenib', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of dabrafenib, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Concomitant use of dabrafenib with potent CYP450 2C8 or 3A4 inducers should generally be avoided.  Otherwise, patients should be closely monitored for potential loss of efficacy of dabrafenib.', 'Metabolism', 'Concomitant use of dabrafenib with potent CYP450 2C8 or 3A4 inducers should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577239, 'Dapsone (topical)', 'Lumacaftor', 'Minor', 'Theoretically, coadministration of dapsone with inducers of CYP450 3A4 such as rifamycins, anticonvulsants, barbiturates, and St. John''s wort may increase the formation of dapsone hydroxylamine, a metabolite associated with hemolysis.', 'DDInter', 1767317477, 'Avoid concomitant use of dapsone hydroxylamine with inducers or inhibitors of CYP450 3A4 as much as possible, and if concomitant use is necessary, closely monitor hematologic correlates.', 'Metabolism', 'Avoid concomitant use of dapsone hydroxylamine with inducers or inhibitors of CYP450 3A4 as much as possible, and if concomitant use is necessary, closely monitor hematologic correlates.', NULL, NULL, 'Minor', 'pharmacodynamic');
