INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577250, 'Digoxin', 'Lumacaftor', 'Moderate', 'Based on in vitro data demonstrating both inhibition and induction of P-glycoprotein (P-gp) efflux transporter, lumacaftor has the potential to alter the serum concentrations of digoxin, a sensitive P-gp substrate.  Additionally, a clinical study with ivacaftor monotherapy showed that ivacaftor is a weak inhibitor of P-gp.', 'DDInter', 1767317477, 'Caution is advised when digoxin is used with lumacaftor/ivacaftor.  Serum digoxin levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of lumacaftor/ivacaftor.  Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Absorption', 'Caution is advised when digoxin is used with lumacaftor/ivacaftor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577251, 'Dihydroergotamine', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577252, 'Diltiazem', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of diltiazem, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered.  Diltiazem dose adjustment should be guided by clinical response if concomitant use with a CYP450 3A4 inducer is necessary.', 'Metabolism', 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577253, 'Donepezil', 'Lumacaftor', 'Moderate', 'Coadministration with drugs that are inducers of the CYP450 3A4 and/or 2D6 enzymatic pathways may decrease the plasma concentrations of donepezil.  The mechanism is accelerated clearance of donepezil due to enhanced CYP450 activities.', 'DDInter', 1767317477, 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', 'Metabolism', 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577254, 'Doravirine', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with potent CYP450 3A4 inducers is considered contraindicated.  At least a 4-week cessation period is recommended prior to initiation of doravirine treatment.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577255, 'Doxorubicin', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of doxorubicin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort.  Close monitoring for potentially reduced efficacy of doxorubicin is recommended if coadministration is required.', 'Metabolism', 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577256, 'Dronedarone', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of dronedarone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of dronedarone with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided.  The extent to which other, less potent CYP450 3A4 inducers may interact with dronedarone is unknown.  Caution is advised if they are used with dronedarone.', 'Metabolism', 'Concomitant use of dronedarone with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577257, 'Droperidol', 'Lumacaftor', 'Moderate', 'Theoretically, potent CYP450 3A4 inducers may decrease serum concentrations of butyrophenones such as benperidol or droperidol.  This interaction has been reported for haloperidol.', 'DDInter', 1767317477, 'The potential for diminished neuroleptic effects of droperidol or benperidol should be considered during coadministration with potent CYP450 3A4 inducers.  Pharmacologic response to these medications should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the droperidol or benperidol dosage adjusted as necessary.  Patients taking potent CYP450 3A4 inducers that are anticonvulsants should be monitored for loss of seizure control.  Additionally, when droperidol or benperidol are used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience seizures or excessive CNS effects that interfere with their normal activities.', 'Metabolism', 'The potential for diminished neuroleptic effects of droperidol or benperidol should be considered during coadministration with potent CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577258, 'Drospirenone', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may significantly decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is lumacaftor induction of the CYP450 3A4-mediated metabolism of estrogens and progestins.', 'DDInter', 1767317477, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.  Female patients of childbearing potential should use an alternative or additional method of contraception.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577259, 'Duvelisib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of duvelisib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of duvelisib with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of duvelisib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577260, 'Efavirenz', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577261, 'Elagolix', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of elagolix, which is a substrate of both the metabolic isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.  Alternative treatments or a dose adjustment of elagolix may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577262, 'Eliglustat', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4.  Eliglustat is also a substrate of P-gp efflux transporter.', 'DDInter', 1767317477, 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', 'Metabolism', 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577263, 'Elvitegravir', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of elvitegravir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of elvitegravir with potent CYP450 3A4 inducers is generally not recommended.  In addition, other antiretroviral agents that are typically combined with elvitegravir may be contraindicated for use with potent CYP450 3A4 inducers.  Please refer to the manufacturer''s labeling for the specific elvitegravir-containing product for more information.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of elvitegravir with potent CYP450 3A4 inducers is generally not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577264, 'Enasidenib', 'Lumacaftor', 'Moderate', 'Based on in vitro data, coadministration with enasidenib may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP).  The proposed mechanism is decreased clearance due to enasidenib -mediated inhibition of OATP1B1/1B3 and BCRP transporters.', 'DDInter', 1767317477, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enasidenib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577265, 'Encorafenib', 'Lumacaftor', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may decrease the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of encorafenib with potent and moderate CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Concomitant use of encorafenib with potent and moderate CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577266, 'Enfortumab vedotin', 'Lumacaftor', 'Minor', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin.  Enfortumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'However, the interaction is not expected to be clinically relevant, since the intact ADC containing the antibody component is required to bind to Nectin-4, an adhesion protein found on the surface of cells, which allows for internalization and cleavage by lysosomal proteases to enable intracellular delivery of MMAE..', 'Metabolism', 'However, the interaction is not expected to be clinically relevant, since the intact ADC containing the antibody component is required to bind to Nectin-4, an adhesion protein found on the surface of cells, which allows for internalization and cleavage by lysosomal proteases to enable intracellular delivery of MMAE.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577267, 'Entrectinib', 'Lumacaftor', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of entrectinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of entrectinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of entrectinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577268, 'Enzalutamide', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317477, 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided.  Moderate inducers such as bosentan, efavirenz, etravirine, modafinil, nafcillin, and nevirapine should also be avoided if possible.  The extent to which other, less potent CYP450 3A4 inducers may interact with enzalutamide is unknown.  Caution is advised if they are used with enzalutamide.', 'Metabolism', 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577269, 'Eplerenone', 'Lumacaftor', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of eplerenone, which is metabolized by the isoenzyme.  According to the product labeling, administration of a single 100 mg dose of eplerenone with the potent CYP450 3A4 inducer St John''s wort resulted in an approximately 30% decrease in eplerenone systemic exposure (AUC).', 'DDInter', 1767317477, 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of eplerenone, which is metabolized by the isoenzyme.  According to the product labeling, administration of a single 100 mg dose of eplerenone with the potent CYP450 3A4 inducer St John''s wort resulted in an approximately 30% decrease in eplerenone systemic exposure (AUC).  This is not considered clinically significant.', 'Metabolism', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of eplerenone, which is metabolized by the isoenzyme.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577270, 'Eravacycline', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of eravacycline, which undergoes oxidation by CYP450 3A4 and flavin monoxygenase (FMO).', 'DDInter', 1767317477, 'Eravacycline dosage should be increased to 1.5 mg/kg every 12 hours when used with potent CYP450 3A4 inducers.  No dosage adjustment is warranted when eravacycline is used with a weak or moderate inducer.', 'Metabolism', 'Eravacycline dosage should be increased to 1.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577271, 'Erdafitinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib.  The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', 'Metabolism', 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577272, 'Ergometrine', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577273, 'Ergotamine', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577274, 'Erlotinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of erlotinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 (approximately 80% to 95%) and to a lesser extent by CYP450 1A2.', 'DDInter', 1767317477, 'Concomitant use of erlotinib with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is required, the manufacturer recommends increasing the dosage of erlotinib by 50 mg increments at 2-week intervals up to a maximum of 450 mg as tolerated.  Close clinical and laboratory monitoring for altered efficacy and safety, including renal and liver functions and serum electrolytes, is recommended.  Patients who receive an increased dose of erlotinib will need a dosage reduction upon discontinuation of the inducer.', 'Metabolism', 'Concomitant use of erlotinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577275, 'Erythromycin', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  Lumacaftor is a potent CYP450 3A4 inducer in vivo.', 'DDInter', 1767317477, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.g., oral midazolam, triazolam, lovastatin, simvastatin) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus).  Lumacaftor should also be avoided in patients receiving certain antibiotics (e.g., clarithromycin, erythromycin, telithromycin) and azole antifungal agents (e.g., itraconazole, ketoconazole, posaconazole, voriconazole).  Alternatives to these agents should be considered.  Some authorities recommend avoiding coadministration of lumacaftor-ivacaftor and itraconazole from 2 weeks before, during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577276, 'Escitalopram', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577277, 'Esomeprazole', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577278, 'Esterified estrogens', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins.  Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.  The interaction has been reported primarily with oral contraceptives.', 'DDInter', 1767317477, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.  For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers.  Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).  Individual product labeling should be consulted for specific time frames.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Patients should be advised to notify their physician if they experience inadequate control of symptoms associated with estrogen deficiency (e.g., nocturnal sweating, vasomotor disturbances, atrophic vaginitis) or changes in the uterine bleeding profile.', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
