INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577284, 'Ethinylestradiol', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may significantly decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is lumacaftor induction of the CYP450 3A4-mediated metabolism of estrogens and progestins.', 'DDInter', 1767317477, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.  Female patients of childbearing potential should use an alternative or additional method of contraception.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.', NULL, NULL, 'Major', 'pharmacodynamic');
