INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577286, 'Everolimus', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 1767317477, 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.  Alternative therapeutic agents with less enzyme induction potential should be considered.  Some manufacturers recommend that, if concomitant use is unavoidable, the daily everolimus dose should be doubled to achieve the recommended therapeutic range for the condition being treated.  Please refer to the manufacturer''s labeling for specific dosing information.  If the potent CYP450 3A4 and/or P-gp inducer is discontinued, the everolimus dosage should be returned to the dosage used before the potent inducer was commenced after a washout period of approximately 3 to 5 days.  Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase, and 2 weeks after discontinuation of the potent inducers.', 'Metabolism', 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577287, 'Exemestane', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of exemestane, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort.  However, it has also been suggested that suppression of estrogen levels is not affected by the interaction, thus dosage adjustment of exemestane is not required.  The extent to which other, less potent CYP450 3A4 inducers may interact with exemestane is unknown.  Caution is advised if they are used with exemestane.', 'Metabolism', 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577288, 'Fedratinib', 'Lumacaftor', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of fedratinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19 and flavin-containing monooxygenase 3 (FMO3).', 'DDInter', 1767317477, 'Concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577289, 'Felbamate', 'Lumacaftor', 'Moderate', 'Coadministration with potent CYP450 3A4 inducers may decrease the plasma concentrations of felbamate, which may be a substrate of the isoenzyme.  Pharmacokinetic data may not be available for all potent inducers with respect to their effect on felbamate.  However, the potent CYP450 3A4 inducers carbamazepine, phenobarbital, and phenytoin have all been shown to decrease felbamate plasma concentrations.', 'DDInter', 1767317477, 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inducer is added to or withdrawn from therapy with felbamate.', 'Metabolism', 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577290, 'Felodipine', 'Lumacaftor', 'Major', 'Potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of calcium channel blockers, the majority of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation or discontinuation of the CYP450 3A4 inducer, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577291, 'Fentanyl', 'Lumacaftor', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone.  Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer.  Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317477, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577292, 'Fesoterodine', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'No dosing adjustments are recommended when fesoterodine is used with CYP450 3A4 inducers.  However, the possibility of diminished therapeutic effects should be considered.', 'Metabolism', 'No dosing adjustments are recommended when fesoterodine is used with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577293, 'Fexofenadine', 'Lumacaftor', 'Moderate', 'Based on in vitro data demonstrating both inhibition and induction of P-glycoprotein (P-gp) efflux transporter, lumacaftor has the potential to alter the plasma concentrations of drugs that are substrates of P-gp.  Additionally, a clinical study with ivacaftor monotherapy showed that ivacaftor is a weak inhibitor of P-gp.  Therefore, concomitant use of lumacaftor/ivacaftor with P-gp substrates may alter the systemic exposure to these substrates.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor is used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised when lumacaftor is used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
