INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577299, 'Fostamatinib', 'Lumacaftor', 'Major', 'Coadministration of fostamatinib with potent inducers of CYP450 3A4 may significantly decrease exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration.  Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9.', 'DDInter', 1767317477, 'Concomitant use of fostamatinib with potent CYP450 3A4 inducers is not recommended.', 'Metabolism', 'Concomitant use of fostamatinib with potent CYP450 3A4 inducers is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577300, 'Fostemsavir', 'Lumacaftor', 'Major', 'Coadministration of fostemsavir with potent CYP450 3A4 inducers may significantly decrease the plasma concentrations of temsavir, the active moiety of fostemsavir.  According to the prescribing information, temsavir is a substrate of CYP450 3A4, esterases, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of fostemsavir with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of fostemsavir with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577301, 'Gefitinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of gefitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'When used concomitantly with potent CYP450 3A4 inducers, the manufacturer recommends increasing the dosage of gefitinib to 500 mg daily in the absence of severe adverse reactions.  The normal recommended dosage of 250 mg once daily may be resumed 7 days after discontinuing the CYP450 3A4 inducer.', 'Metabolism', 'When used concomitantly with potent CYP450 3A4 inducers, the manufacturer recommends increasing the dosage of gefitinib to 500 mg daily in the absence of severe adverse reactions.', NULL, NULL, 'Major', 'pharmacodynamic');
