INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577314, 'Imatinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of imatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of imatinib with potent CYP450 3A4 inducers should generally be avoided.  Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible.  If coadministration with a potent CYP450 3A4 inducer is required, some authorities recommend increasing the dosage of imatinib by at least 50% while carefully monitoring clinical response.  Imatinib dosages up to 1200 mg/day (600 mg twice daily) have been given to patients receiving concomitant potent CYP450 3A4 inducers.', 'Metabolism', 'Concomitant use of imatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
