INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577319, 'Irinotecan', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38.  Irinotecan is partially metabolized by CYP450 3A4 to inactive substances, and induction of this process results in less of the drug available for conversion to SN-38 via hepatic carboxylesterases.  Available data also suggest induction of other enzymatic pathways (e.g., UGT1A1; carboxylesterases) and drug transporters (e.g., multispecific organic anion transporter, or MRP2; mitoxantrone-resistance half transporter protein, or MXR) that may be involved in the clearance of irinotecan and/or SN-38, although the extent to which they contribute to the interaction is unknown.', 'DDInter', 1767317477, 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided.  Consideration should be given to substituting nonenzyme-inducing agents at least one to two weeks prior to initiation of irinotecan therapy whenever possible.', 'Metabolism', 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
