INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577331, 'Lumacaftor', 'Lapatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is required, a gradual dosage increase of lapatinib should be considered.  The prescribing information recommends titrating the lapatinib dosage gradually from 1250 mg/day up to 4500 mg/day or from 1500 mg/day up to 5500 mg/day depending on the indication and patient tolerability.  Based on pharmacokinetic studies, this dosage recommendation is predicted to adjust the lapatinib systemic exposure (AUC) to the range observed without inducers.  However, clinical data are lacking.  The dosage of lapatinib should be reduced over approximately 2 weeks to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577332, 'Lumacaftor', 'Larotrectinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of larotrectinib.  According to the prescribing information, larotrectinib is metabolized primarily by the CYP450 3A4 isoenzyme and is a substrate of the P-gp and BCRP efflux transporters in vitro.', 'DDInter', 1767317477, 'Concomitant use of larotrectinib with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.  If coadministration is required, the manufacturer recommends doubling the larotrectinib dose.  After the inducer has been discontinued for 3 to 5 elimination half-lives, the larotrectinib dose that was taken prior to initiating the inducer may be resumed.', 'Metabolism', 'Concomitant use of larotrectinib with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577333, 'Lumacaftor', 'Lefamulin', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'Concomitant use of lefamulin with potent or moderate inducers of CYP450 3A4 and/or P-gp should be avoided unless the benefit outweighs the risks.  Monitor for reduced efficacy of lefamulin if coadministration is necessary.', 'Metabolism', 'Concomitant use of lefamulin with potent or moderate inducers of CYP450 3A4 and/or P-gp should be avoided unless the benefit outweighs the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
