INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577336, 'Lumacaftor', 'Levacetylmethadol', 'Major', 'Coadministration with inducers of CYP450 3A4 may increase the peak activity and/or decrease the duration of action of levomethadyl acetate, which is primarily metabolized by the isoenzyme.  Induction of CYP450 3A4 also results in increased formation of the active metabolites, nor-LAAM and dinor-LAAM.  High plasma levels of these metabolites may increase the risk of QT interval prolongation and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317477, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort.  The dose or dosing interval may require adjustment if an interaction is suspected.  A 12-lead ECG should be performed before initiating levomethadyl acetate therapy, 12 to 14 days after initiating therapy, and periodically thereafter.  Patients should be advised to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577337, 'Lumacaftor', 'Levonorgestrel', 'Major', 'Coadministration with lumacaftor may significantly decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is lumacaftor induction of the CYP450 3A4-mediated metabolism of estrogens and progestins.', 'DDInter', 1767317477, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.  Female patients of childbearing potential should use an alternative or additional method of contraception.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577338, 'Lumacaftor', 'Lidocaine', 'Minor', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of lidocaine, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Because lidocaine is not available commercially for oral administration, the interaction may be of limited clinical relevance.', 'Metabolism', 'Because lidocaine is not available commercially for oral administration, the interaction may be of limited clinical relevance.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577339, 'Lumacaftor', 'Linagliptin', 'Moderate', 'Coadministration with potent inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may significantly decrease the plasma concentrations of linagliptin, which is a substrate for both.', 'DDInter', 1767317477, 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided.  For patients requiring use of such drugs, an alternative to linagliptin is strongly recommended.  Other known inducers of CYP450 3A4 include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, modafinil, nafcillin, nevirapine, and various other anticonvulsants, although the extent to which they interact with linagliptin is unknown.  If concomitant use with these agents is necessary, glycemic control should be closely monitored.', 'Metabolism', 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
