INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577373, 'Oliceridine', 'Lumacaftor', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone.  Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer.  Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317477, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577374, 'Omeprazole', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577375, 'Osilodrostat', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or 2B6 may decrease the plasma concentrations of osilodrostat, which is partially metabolized by these isoenzymes.', 'DDInter', 1767317477, 'An increase in the dosage of osilodrostat may be required during concomitant use of a potent CYP450 3A4 and/or 2B6 inducer.  Dosage adjustments should be based on clinical response and tolerance.  Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations of their signs and symptoms.  A reduction in dosage of osilodrostat may be required if the potent CYP450 3A4 and/or 2B6 inducer is discontinued during treatment with osilodrostat.', 'Metabolism', 'An increase in the dosage of osilodrostat may be required during concomitant use of a potent CYP450 3A4 and/or 2B6 inducer.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577376, 'Osimertinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of osimertinib with potent CYP450 3A4 inducers should generally be avoided.  However, if concomitant use is required, some authorities recommend increasing the dose of osimertinib to 160 mg daily during treatment with the potent 3A4 inducer and resumed at 80 mg daily, 3 weeks after discontinuation of the potent 3A4 inducer.', 'Metabolism', 'Concomitant use of osimertinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577377, 'Ospemifene', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4, 2C9, and/or 2C19 may decrease the plasma concentrations of ospemifene, which is metabolized by these isoenzymes.  The interaction has been studied with rifampin, a potent CYP450 3A4/moderate CYP450 2C9/moderate CYP450 2C19 inducer.', 'DDInter', 1767317477, 'The potential for reduced therapeutic effects of ospemifene should be considered when used in combination with potent inducers of CYP450 3A4, 2C9, and/or 2C19.', 'Metabolism', 'The potential for reduced therapeutic effects of ospemifene should be considered when used in combination with potent inducers of CYP450 3A4, 2C9, and/or 2C19.', NULL, NULL, 'Moderate', 'pharmacodynamic');
