INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577417, 'Riociguat', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.  Patients should be closely monitored, and the dosage of riociguat adjusted as necessary.  Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort.  Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577418, 'Ripretinib', 'Lumacaftor', 'Major', 'Coadministration with strong inducers of CYP450 3A4 may decrease the plasma concentrations and anti-tumor activities of ripretinib and its active metabolite (DP-5439).  The proposed mechanism is induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', 'DDInter', 1767317477, 'Concomitant use of ripretinib with a strong CYP450 3A4 inducer is not recommended.  Loss of anti-tumor activity may occur.', 'Metabolism', 'Concomitant use of ripretinib with a strong CYP450 3A4 inducer is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577419, 'Risperidone', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of risperidone and its active metabolite, 9-hydroxyrisperidone.  Both risperidone and 9-hydroxyrisperidone are substrates of the CYP450 3A4 isoenzyme and P-gp efflux transporter.', 'DDInter', 1767317477, 'When risperidone is administered orally, the prescribing information recommends increasing (up to double) the usual dosage gradually (e.g., over 1 to 2 weeks) following the addition of a potent CYP450 3A4/P-gp inducer.  For extended-release injectable formulations of risperidone, patients should be closely monitored for the first 4 to 8 weeks after starting treatment with a potent CYP450 3A4/P-gp inducer.  A dosage increase or supplemental oral risperidone therapy may be required in some cases.  Changes in efficacy and safety should be carefully monitored with any dosage adjustment.  Upon discontinuation of the inducer, risperidone dosage should be reassessed and readjusted accordingly for the anticipated increase in plasma concentrations of risperidone and 9-hydroxyrisperidone.  The prescribing information for individual risperidone products should be consulted for specific recommendations regarding concomitant use with potent CYP450 3A4 inducers.', 'Metabolism', 'When risperidone is administered orally, the prescribing information recommends increasing (up to double) the usual dosage gradually (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577420, 'Ritonavir', 'Lumacaftor', 'Major', 'Coadministration with drugs that are strong inducers of CYP450 3A4 may decrease the plasma concentrations of ritonavir and result in a potential loss of virologic response.  The proposed mechanism is increased clearance due to induction of CYP450 3A4, which is the isoenzyme primarily responsible for the metabolism of ritonavir.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of ritonavir with drugs that are strong inducers of CYP450 3A4 is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of ritonavir with drugs that are strong inducers of CYP450 3A4 is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
