INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577446, 'Tivozanib', 'Lumacaftor', 'Major', 'Coadministration of tivozanib with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of tivozanib, which has been shown to be a substrate of the isoenzyme.', 'DDInter', 1767317477, 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer should generally be avoided.  If coadministration is necessary, some authorities advise caution, with clinical and laboratory monitoring to be considered whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577447, 'Tofacitinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme.  The interaction may result in loss of or reduced clinical response.', 'DDInter', 1767317477, 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.  The extent to which moderate CYP450 3A4 inducers interact with tofacitinib is unknown.', 'Metabolism', 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577448, 'Tolazamide', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', 'DDInter', 1767317477, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
