INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577545, 'Tipiracil', 'Levonorgestrel', 'Moderate', 'Coadministration with tipiracil-trifluridine may reduce the efficacy of contraceptive hormones.', 'DDInter', 1767317477, 'Because trifluridine may cause fetal harm, it is particularly important that patients not become pregnant during tipiracil-trifluridine treatment.  Female patients of childbearing potential using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant tipiracil-trifluridine treatment.  The manufacturer recommends use of additional barrier contraceptive methods during and for up to 6 months after tipiracil-trifluridine therapy.  No recommendations are available for women using hormone-release intrauterine systems or injectable progestin-only contraceptives.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Antagonism', 'Because trifluridine may cause fetal harm, it is particularly important that patients not become pregnant during tipiracil-trifluridine treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577546, 'Trifluridine', 'Measles virus vaccine live attenuated', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
