INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577549, 'Trifluridine', 'Monomethyl fumarate', 'Moderate', 'The use of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with other immunosuppressive or myelosuppressive agents may increase the risk of infections.', 'DDInter', 1767317477, 'The safety and efficacy of monomethyl fumarate (MMF), dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.  Until further information is available, concomitant use should be avoided if possible.  Short courses of intravenous corticosteroids may be used in combination with MMF or its prodrugs for the treatment of multiple sclerosis relapses.  When switching patients from one immunosuppressive or myelosuppressive agent to another, the half-life and mode of action of each therapy should be considered to avoid unintended additive effects on the immune system while simultaneously reducing the risk of reactivation of multiple sclerosis.', 'Synergism', 'The safety and efficacy of monomethyl fumarate (MMF), dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
