INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577641, 'Afatinib', 'Voxilaprevir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of afatinib, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised if afatinib is used in combination with P-gp inhibitors.  Patients should be monitored for potentially increased adverse effects such as diarrhea, which may lead to dehydration with or without renal impairment; cutaneous reactions including rash, erythema, and bullous, blistering, or exfoliating lesions; interstitial lung disease such as lung infiltration, pneumonitis, acute respiratory distress syndrome, and allergic alveolitis; hepatotoxicity, which may be life-threatening or fatal; keratitis characterized by acute or worsening eye inflammation, lacrimation, light sensitivity, blurred vision, eye pain, red eye, and/or ulceration; and left ventricular dysfunction.  The manufacturer recommends reducing the daily dose of afatinib by 10 mg if not tolerated.  The previous dose may be resumed after discontinuation of the P-gp inhibitor as tolerated.', 'Distribution', 'Caution is advised if afatinib is used in combination with P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
