INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577668, 'Ethinylestradiol', 'Voxilaprevir', 'Moderate', 'Coadministration of ethinylestradiol-containing products with sofosbuvir/velpatasvir/voxilaprevir may increase the risk of alanine aminotransferase (ALT) elevations.  The mechanism for this interaction has not been delineated.', 'DDInter', 1767317477, 'Caution and close clinical and laboratory monitoring are recommended if ethinylestradiol-containing products are used in combination with sofosbuvir/velpatasvir/voxilaprevir.  Alternative methods of contraception, such as progestin only contraception or nonhormonal methods, should be considered.', 'Others', 'Caution and close clinical and laboratory monitoring are recommended if ethinylestradiol-containing products are used in combination with sofosbuvir/velpatasvir/voxilaprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577669, 'Everolimus', 'Voxilaprevir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the blood concentrations of everolimus following oral administration.  Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus.', 'DDInter', 1767317477, 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Absorption', 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
