INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577678, 'Idarubicin', 'Voxilaprevir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1.', 'DDInter', 1767317477, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.  Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577679, 'Idelalisib', 'Voxilaprevir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of idelalisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors.  Pharmacologic response to idelalisib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the idelalisib dosing adjusted or interrupted as necessary in accordance with the product labeling.  Patients should be closely monitored for idelalisib toxicity such as hepatotoxicity, diarrhea, colitis, intestinal perforation, pneumonitis, neutropenia, and thrombocytopenia.', 'Metabolism', 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577680, 'Indacaterol', 'Voxilaprevir', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the systemic exposure to indacaterol following oral inhalation, as it is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317477, 'Indacaterol oral inhalation powder has been evaluated in clinical trials for up to one year at doses up to 600 mcg.  No dosage adjustment is necessary at the 75 mcg dose when used with CYP450 3A4 and P-glycoprotein inhibitors.', 'Metabolism', 'Indacaterol oral inhalation powder has been evaluated in clinical trials for up to one year at doses up to 600 mcg.', NULL, NULL, 'Minor', 'pharmacodynamic');
