INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577699, 'Voxilaprevir', 'Lovastatin', 'Moderate', 'Coadministration with sofosbuvir/velpatasvir/voxilaprevir may increase the plasma concentrations of all statins including atorvastatin, fluvastatin, lovastatin, and simvastatin.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of statins by velpatasvir and voxilaprevir.', 'DDInter', 1767317477, 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir.  The lowest approved dosage of the statin should be used.  If higher dosages are required, the lowest effective statin dosage should be prescribed based on a risk/benefit assessment.', 'Distribution', 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
