INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577784, 'Theophylline', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may decrease the serum concentrations of theophylline.  The proposed mechanism is accelerated clearance of theophylline due to induction of hepatic CYP450 isoenzymes, namely 1A2, by constituents of St. John''s wort.', 'DDInter', 1767317477, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.  Pharmacologic response and serum theophylline levels should be monitored more closely whenever St. John''s wort is added to or withdrawn from therapy, and the methylxanthine dosage adjusted as necessary.  Patients should be advised to notify their physician if they experience loss of asthma control or worsening of respiratory symptoms.', 'Metabolism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1577785, 'Gadoxetic acid', 'Timolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral contrast media.  In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.  The mechanism is unknown.  Theoretically, beta-blocker ophthalmic solutions may also interact, as they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'DDInter', 1767317477, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to contrast media.  If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine.  Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm.  Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', 'Patients who have received beta-blockers should be closely monitored for adverse reactions to contrast media.', NULL, NULL, 'Moderate', 'pharmacodynamic');
