INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (110, 6795, 'Ketamine', 'Alcohol may potentiate some of the pharmacologic effects of ketamine.  Use in combination may result in additive central nervous system (CNS) depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving ketamine should not drink alcohol.  Caution is advised when ketamine is used in patients with acute alcohol intoxication or a history of chronic alcoholism.  Following anesthesia with ketamine, patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination, such as driving or operating hazardous machinery, for at least 24 hours and until they know how the medication affects them.  Patients treated with oral ketamine should also avoid consumption of grapefruit and grapefruit juice during treatment.  Otherwise, dosage reductions of oral ketamine should be considered.', '', '', 'DDInter', 0);
